// Función básica para cargar STL en la escena usando Three.js
function loadSTL(filePath) {
    if (!window.scene) {
        alert('No se encontró la escena Three.js (scene)');
        return;
    }
    // Elimina el último objeto STL cargado si existe
    // if (window.lastSTLObject) {
    //     window.scene.remove(window.lastSTLObject);
    //     window.lastSTLObject.geometry.dispose();
    //     if (window.lastSTLObject.material) window.lastSTLObject.material.dispose();
    //     window.lastSTLObject = null;
    // }
    const loader = new THREE.STLLoader();
    loader.load(filePath, function (geometry) {
        geometry.computeBoundingBox();
        // Generar UVs para que la textura se vea bien
        if (typeof generateBoxUVs === "function") {
            generateBoxUVs(geometry);
        }
        const box = geometry.boundingBox;
        const center = new THREE.Vector3();
        box.getCenter(center);
        geometry.translate(-center.x, -center.y, -center.z);
        geometry.computeVertexNormals();

        // Si el STL tiene grupos, asigna materiales alternos para resaltar detalles
        let mesh;
        if (geometry.groups && geometry.groups.length > 1) {
            // Crea materiales alternos para cada grupo
            const materials = [
                new THREE.MeshStandardMaterial({ color: 0x888888, metalness: 0.2, roughness: 0.7, flatShading: false, side: THREE.DoubleSide, depthWrite: true, depthTest: true }),
                new THREE.MeshStandardMaterial({ color: 0xcccccc, metalness: 0.2, roughness: 0.7, flatShading: false, side: THREE.DoubleSide, depthWrite: true, depthTest: true })
            ];
            mesh = new THREE.Mesh(geometry, materials);
        } else {
            // Un solo material, pero con wireframe para ver detalles
            const material = new THREE.MeshStandardMaterial({
                color: 0x888888,
                metalness: 0.2,
                roughness: 0.7,
                flatShading: false,
                side: THREE.DoubleSide,
                depthWrite: true,
                depthTest: true,
                wireframe: false // Cambia a true si quieres ver la malla
            });
            mesh = new THREE.Mesh(geometry, material);
        }
        mesh.castShadow = true;
        mesh.receiveShadow = true;
        mesh.scale.set(0.1, 0.1, 0.1);
        window.scene.add(mesh);
        window.lastSTLObject = mesh;

        // Calcular y mostrar dimensiones escaladas
        const scaledBox = new THREE.Box3().setFromObject(mesh);
        const scaledSize = scaledBox.getSize(new THREE.Vector3());
        if (typeof showToast === "function") {
            showToast(
                `Medidas del módulo (escaladas):<br>` +
                `Ancho: ${scaledSize.x.toFixed(2)}<br>` +
                `Alto: ${scaledSize.y.toFixed(2)}<br>` +
                `Profundidad: ${scaledSize.z.toFixed(2)}`
            );
        } else {
            alert(
                `Medidas del módulo (escaladas):\n` +
                `Ancho: ${scaledSize.x.toFixed(2)}\n` +
                `Alto: ${scaledSize.y.toFixed(2)}\n` +
                `Profundidad: ${scaledSize.z.toFixed(2)}`
            );
        }

        if (window.camera && window.controls) {
            const boxWorld = new THREE.Box3().setFromObject(mesh); 
            const centerWorld = boxWorld.getCenter(new THREE.Vector3());
            window.controls.target.copy(centerWorld);
            const size = boxWorld.getSize(new THREE.Vector3()).length();
            const distance = size * 1.5 + 50;
            window.camera.position.set(centerWorld.x + distance, centerWorld.y + distance, centerWorld.z + distance);
            window.camera.lookAt(centerWorld);
            window.controls.update();
        }
    }, undefined, function (error) {
        alert('Error cargando STL: ' + error);
        console.error('Error cargando STL:', error);
    });
}

// Construye el menú tipo árbol para navegar por modulos_predeterminados
function construirMenuModulos() {
    const treeDiv = document.getElementById('modulosTree');
    if (!treeDiv) return;
    treeDiv.innerHTML = '';
    // Primer nivel: carpetas dentro de modulos_predeterminados
    fetch('/api/listar-contenido?path=')
        .then(res => res.json())
        .then(data => {
            if (!data.carpetas) return;
            data.carpetas.forEach(nombreCarpeta => {
                const carpetaDiv = document.createElement('div');
                carpetaDiv.style.marginBottom = '6px';
                carpetaDiv.innerHTML = `
                    <b style="cursor:pointer;color:#6cf;" onclick="mostrarContenidoCarpeta(this, '${nombreCarpeta}')">
                        📁 ${nombreCarpeta}
                    </b>
                    <div class="contenido-lista" style="margin-left:16px;display:none;"></div>
                `;
                treeDiv.appendChild(carpetaDiv);
            });
        });
}

// Función recursiva para mostrar contenido de una carpeta
window.mostrarContenidoCarpeta = function (el, relPath) {
    const lista = el.parentElement.querySelector('.contenido-lista');
    if (lista.style.display === 'block') {
        lista.style.display = 'none';
        lista.innerHTML = '';
        return;
    }
    lista.style.display = 'block';
    lista.innerHTML = 'Cargando...';
    fetch(`/api/listar-contenido?path=${encodeURIComponent(relPath)}`)
        .then(res => res.json())
        .then(data => {
            lista.innerHTML = '';
            // Subcarpetas
            data.carpetas.forEach(subcarpeta => {
                const subPath = relPath ? `${relPath}/${subcarpeta}` : subcarpeta;
                const subDiv = document.createElement('div');
                subDiv.innerHTML = `
                    <span class="folder-item" onclick="mostrarContenidoCarpeta(this, '${subPath}')">
                        <span class="folder-icon">📁</span>${subcarpeta}
                    </span>
                    <div class="contenido-lista" style="margin-left:16px;display:none;"></div>
                `;
                lista.appendChild(subDiv);
            });
            // Archivos
            data.archivos.forEach(archivo => {
                const fileDiv = document.createElement('div');
                fileDiv.className = 'file-item';
                fileDiv.innerHTML = `<span class="file-icon">📄</span>${archivo}`;
                fileDiv.onclick = () => cargarModuloAlProyecto('modulos_predeterminados/' + relPath + '/' + archivo);
                lista.appendChild(fileDiv);
            });
        })
        .catch(() => {
            lista.innerHTML = 'Error al cargar contenido.';
        });
};

// Llama a construirMenuModulos cuando cargue la página
window.addEventListener('DOMContentLoaded', construirMenuModulos);

// Función para cargar el STL seleccionado desde el árbol
function cargarModuloAlProyecto(filePath) {
    loadSTL(filePath);
}

